package program;

public class NumberLoop2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0;i<4;i++) {
			
			for(int j=1;j<=i+1;j++ ) {
				System.out.print(j);
				System.out.print("\t");
			//	k++;
				
			}
			System.out.println(" ");
			
		}
	}

}
